  //       <li><a>About</a></li>
  //       <li><a>Contact</a></li>

  //     </ul>

  //   menuMask = 


  // }

  let colourInvert = document.querySelectorAll('.header__utilitiesSVG:last-of-type');

  function flipColours() {
    var colourOne = document.documentElement.style.getPropertyValue('--primaryColour');
    var colourTwo = document.documentElement.style.getPropertyValue('--backgroundColour');

    document.documentElement.style.setProperty('--primaryColour', colourTwo);
    document.documentElement.style.setProperty('--backgroundColour', colourOne);
  };

  colourInvert.addEventListener("click", flipColours());


  return (
    <div className="header__utilities">
      <img className="header__utilitiesSVG" src={searchLogo} alt={searchLogo} />
      <img className="header__utilitiesSVG" src={colourInverter} alt={colourInverter} />
      <Navigation />
    </div>

  );

}



export default Utilities;
