import './MobileNavMotion.js';
import './header.css';
import './main.css';
import Search from './static/search.svg';
// import { SideBarData } from './SideBarData';



function Navigation() {

  const [sideBar, setSideBar] = useState(false);

  const showSideBar = () => setSideBar(!sideBar);

  return (

    <div>

      <nav className='header__mobileNavToggle'>
        <img src={menuIcon} alt={menuIcon} onClick={showSideBar} ></img>
      </nav>

      <nav className={sideBar ? 'header__mobileNav--active' : 'header__mobileNav'}>
        <ul className='header__mobileNavList'>
          <li className='header__mobileNavItem'>Latest News</li>
          <li className='header__mobileNavItem'>About</li>
          <li className='header__mobileNavItem borderBottom1px'>Contact</li>
          <li className='header__mobileNavItem'><input className='button--underlined' placeholder='Search news' /></li>
          <li className='header__mobileNavItem borderBottom1px'><button className='button--primary'>Sign In</button></li>
          <li className='header__mobileNavItem' onClick={showSideBar}>Close</li>
        </ul>
      </nav>


    </div>



  )



}

